INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,1,'Rabies 1-year (Adult)', 'Single dose', 'Annual boosters are required.', 'Core dog vaccine. Rabies is 100% fatal to dogs, with no treatment available. Prevention is key.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,1,'Rabies 3-year (Adult)', 'Single dose', 'A second vaccination is recommended after 1 year, then boosters every 3 years.', 'Core dog vaccine.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,1,'Distemper (Adult)', '2 doses, given 3-4 weeks apart', 'Puppies need a booster 1 year after completing their initial series, then all dogs need a booster every 3 years or more often.', 'Core dog vaccine. Caused by an airborne virus, distemper is a severe disease that, among other problems, may cause permanent brain damage.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,1,'Parvovirus (Adult)', '2 doses, 3-4 weeks apart', 'Puppies need a booster 1 year after completing the initial series, then all dogs need a booster every 3 years or more often.', 'Core dog vaccine. Canine "parvo" is contagious, and can cause severe vomiting and bloody diarrhea. Parvo is usually fatal if untreated.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,1,'Adenovirus, type 1 (CAV-1, canine hepatitis) (Adult)', 'depends on vaccine', 'Puppies need a booster 1 year after completing the initial series, then all dogs need a booster every 3 years or more often.', 'Core dog vaccine. Spread via infected saliva, urine and feces; canine hepatitis can lead to severe liver damage, and death.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,0,'Adenovirus, type 2 (CAV-2, kennel cough) (Adult)', '2 doses, 3-4 weeks apart', 'Puppies need a booster 1 year after completing the initial series, then all dogs need a booster every 3 years or more often.', 'Core dog vaccine. Spread via coughs and sneezes.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,0,'Parainfluenza (Adult)', '1 dose', 'A booster may be necessary after 1 year, depending on manufacturer recommendations; revaccination every 3 years is considered protective.', 'Non-core dog vaccine. Parainfluenza infection (not the same as canine influenza) results in cough, fever. It may be associated with Bordetella infection.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,0,'Bordetella bronchiseptica (kennel cough) (Adult)', '1 dose of the intranasal or oral product, or 2 doses of the injected product', 'Annual or 6-month boosters may be recommended for dogs in high-risk environments.', 'Non-core dog vaccine. Not usually a serious condition, although it can be dangerous in young puppies. It is usually seen after activities like boarding or showing.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,0,'Lyme disease (Adult)', '2 doses, 2-4 weeks apart', 'May be needed annually, prior to the start of tick season', 'Non-core dog vaccine. Generally recommended only for dogs with a high risk for exposure to Lyme disease-carrying ticks.');
INSERT INTO vaccPerSpecies(speciesID, adult, core, vaccName, dosesNote, boosterNote, comment) VALUES(1,1,0,'Leptospirosis (Adult)', '2 doses, 2-4 weeks apart', 'At least once yearly for dogs in high-risk areas', 'Non-core dog vaccine. Vaccination is generally restricted to established risk areas. Exposure to rodents and standing water can lead to a leptospirosis infection.');
